+        #region GetCommonDiagnosticCodeAssignmentModels
+
+        /// <summary>
+        /// Gets the common therapy observation index models.
+        /// </summary>
+        /// <param name="filter">The filter.</param>
+        /// <returns></returns>
+        public IList<CommonDiagnosticCodeAssignmentModel> GetCommonDiagnosticCodeAssignmentModels(CommonDiagnosticCodeIndexFilter filter)
+        {
+            if (!filter.SelectedProviderTypeId.IsPositive())
+                return new List<CommonDiagnosticCodeAssignmentModel>();
+
+            filter.Type = DiagnosticCodeType.ICD10;
+
+            var filterDiagnosticCodes = new CommonDiagnosticCodeFilter(DataServiceFactory.AccountService.GetAccountFilter())
+            {
+                Type = filter.Type,
+                SelectedProviderTypeId = filter.SelectedProviderTypeId,
+                SelectedDistrictId = filter.SelectedDistrictId
+            };
+
+            var diagnosticCodes = DataServiceFactory.CommonDiagnosticCodeDataService.GetDiagnosticCodeModels(filterDiagnosticCodes);
+            var existingIndexes = GetExistingIndexes(filter);
+            var therapyIndexLookup = diagnosticCodes.Select(dCode =>
+            {
+                var existingAssignment = existingIndexes.FirstOrDefault(x => x.CommonDiagnosticCodeId == dCode.CommonDiagnosticCodeId);
+                return new CommonDiagnosticCodeAssignmentModel
+                {
+                    CommonDiagnosticCodeId = dCode.CommonDiagnosticCodeId,
+                    CommonDiagnosticCodeIndexId = existingAssignment?.CommonDiagnosticCodeIndexId ?? null,
+                    Name = dCode.Code + " - " + dCode.Name,
+                    CommonProviderTypeId = filter.SelectedProviderTypeId.Value,
+                    CommonDistrictId = filter.DistrictId,
+                    IsDisabled = existingAssignment?.IsDisabled ?? false,
+                    IsAllowed = existingAssignment != null
+                };
+            }).ToList();
+
+            return therapyIndexLookup;
+        }
+
+        /// <summary>
+        /// Gets the common therapy observation index models.
+        /// </summary>
+        /// <param name="filter">The filter.</param>
+        /// <returns></returns>
+        private IList<CommonDiagnosticCodeIndex> GetExistingIndexes(CommonDiagnosticCodeIndexFilter filter)
+        {
+            var baseQuery = QueryBuilderFactory.CommonDiagnosticCodeIndexQueryBuilder.GetDiagnosticCodeIndexes(filter);
+            var items = DataServiceFactory.GetRepository<CommonDiagnosticCodeIndex>().Query<CommonDiagnosticCodeIndex>(baseQuery).ToList();
+            return items;
+        }
+
+        #endregion
+
+        #region SaveAssignments
+
+        /// <summary>
+        /// Saves the common diagnostic code indexes.
+        /// </summary>
+        /// <param name="model">The model.</param>
+        /// <returns></returns>
+        public async Task<bool> SaveDiagnosticCodeIndex(IList<CommonDiagnosticCodeAssignmentModel> model)
+        {
+            var providerTypeId = model[0].CommonProviderTypeId;
+            var districtId = model[0]?.CommonDistrictId ?? 0;
+
+            if (model.Any(x => x.CommonProviderTypeId != providerTypeId || x.CommonDistrictId != districtId))
+                return false;
+
+            var filter = new CommonDiagnosticCodeIndexFilter()
+            {
+                SelectedProviderTypeId = providerTypeId,
+                DistrictId = districtId,
+                Type = DiagnosticCodeType.ICD10
+            };
+
+            var indexes = GetExistingIndexes(filter);
+            var existingDiagnosticCodeIndexIds = indexes.Select(x => x.CommonDiagnosticCodeIndexId).ToList();
+            if (existingDiagnosticCodeIndexIds.HasItems())
+                await DataServiceFactory.GetRepository<CommonDiagnosticCodeIndex>().DeleteListAsync(existingDiagnosticCodeIndexIds);
+
+            var saveTasks = model
+                .Where(x => x.IsAllowed)
+                .Select(async x =>
+                {
+                    var commonDiagnosticCodeIndex = new CommonDiagnosticCodeIndex
+                    {
+                        CommonDistrictId = districtId,
+                        CommonProviderTypeId = providerTypeId,
+                        CommonDiagnosticCodeId = x.CommonDiagnosticCodeId,
+                        IsDisabled = x.IsDisabled,
+                        CreatedDate = DateTime.Now,
+                        CreatedBy = DataServiceFactory.AccountService.CurrentUser.UserName,
+                        UpdatedDate = DateTime.Now,
+                        UpdatedBy = DataServiceFactory.AccountService.CurrentUser.UserName
+                    };
+                    return await DataServiceFactory.GetRepository<CommonDiagnosticCodeIndex>().InsertAsync(commonDiagnosticCodeIndex);
+                });
+            await Task.WhenAll(saveTasks);
+
+            return true;
+        }
+        #endregion
+
         #endregion
     }
 }
