+﻿"use strict";
+
+angular
+    .module('cheetah.ctrls')
+    .controller('diagnosticCodeTabAssignmentsCtrl', [
+        '$controller', '$rootScope', '$scope', 'adminDiagnosticCodeService', 'kendoService', 'lookupService', 'confirmationService',
+        function ($controller, $rootScope, $scope, adminDiagnosticCodeService, kendoService, lookupService, confirmationService) {
+            angular.extend($scope,
+                {
+                    dsProviderTypes: kendoService.createLookupDataSource(lookupService.providerTypes),
+                    gridDataSource: getGridDataSource(),
+                    gridOptions: getGridOptions(),
+                    save: save,
+                    onAllowedChange: onAllowedChange
+                });
+
+            $scope.$on("diagnosticCodeTabAssignments-bind", bindGrid);
+
+            $scope.$watch("model.CommonProviderTypeId", function (newValue) {
+                if (!newValue)
+                    return;
+
+                $scope.gridDataSource.read();
+            });
+
+            function bindGrid() {
+                if (!$scope.tabsContainer || !$scope.tabsContainer.commonUserId)
+                    return;
+
+                $scope.gridDataSource.read();
+            }
+
+            function getGridDataSource() {
+                return new kendo.data.DataSource({
+                    transport: {
+                        read: function (options) {
+                            var districtId = 0;
+                            if ($scope.model.IsCurrentDistrictOnly)
+                                districtId = $rootScope.options.currentDistrictId;
+
+                            options.filter = {
+                                SelectedProviderTypeId: $scope.model.CommonProviderTypeId,
+                                DistrictId: districtId,
+                            };
+                            kendoService.readGridDataSource(options, adminDiagnosticCodeService.getCommonDiagnosticCodeIndex);
+                        }
+                    },
+                    requestEnd: function (e) { kendoService.endDataSourceRequest($scope.gridDataSource, e); },
+                    schema: {
+                        model: {
+                            id: "CommonDiagnosticCodeId"
+                        },
+                        data: kendoService.schemaData,
+                        total: kendoService.schemaTotal
+                    },
+                    sort: { field: "Name", dir: "asc" },
+                    pageSize: 25
+                });
+            }
+
+            function getGridOptions() {
+                return {
+                    autoBind: false,
+                    sortable: { mode: "single", allowUnsort: false },
+                    pageable: { buttonCount: 5, pageSizes: [5, 10, 20, 50], refresh: true },
+                    scrollable: false,
+                    resizable: false,
+                    dataBound: function (e) {
+                        kendoService.gridDataBound(e);
+                    },
+                    columns: [
+                        { field: "", title: "", width: "30px", checkBoxSelectAll: true, template: "<input type='checkbox' ng-change='onAllowedChange(dataItem)' ng-model='dataItem.IsAllowed' " },
+                        { field: "Name", title: $rootScope.options.stateConfiguration.AcceliTrack.Resources.Name },
+                        { field: "Disabled", title: $rootScope.options.stateConfiguration.AcceliTrack.Resources.Disabled, width: "30px", template: "<input type='checkbox' ng-disabled='!dataItem.IsAllowed'  ng-model='dataItem.IsDisabled' " },
+                    ]
+                };
+            }
+
+            function onAllowedChange(dataItem) {
+                if (!dataItem.IsAllowed)
+                    dataItem.IsDisabled = false;
+            }
+
+            $scope.districtChanged = function () {
+                $scope.gridDataSource.read();
+            }
+
+            function save() {
+                var model = _.map($scope.gridDataSource.data());
+                model = model.filter(obj => obj.IsAllowed);
+
+                adminDiagnosticCodeService
